const tweetText = items[0].json.text || '';
const userName = items[0].json.user_name || 'Unknown';
const notionId = $node['Notion Trigger'].json.id;
const notionUrl = $node['Notion Trigger'].json.URL;

const prompt = `Analyze this tweet about nano banana pro prompts and provide a structured response.

Tweet Author: ${userName}
Tweet Text: ${tweetText}

Provide your response in this exact JSON format:
{
  "category": "<one of: Technique|Use Case|Tips|Examples|Tutorial|Inspiration|Discussion>",
  "tags": ["<tag1>", "<tag2>", "<tag3>"],
  "description": "<concise 1-2 sentence description>",
  "prompt": "<the actual nano banana pro prompt if present, otherwise empty string>",
  "author": "${userName}"
}

IMPORTANT - Prompt Extraction:
- Look for the actual nano banana pro prompt in the tweet
- Prompts are often in quotes, code blocks, or clearly formatted
- Extract the complete prompt text exactly as written
- If the tweet discusses prompts but doesn't contain an actual prompt, leave empty
- If multiple prompts, extract the primary/main one

Focus on:
- What aspect of nano banana pro prompts this covers
- Key techniques or insights mentioned
- Practical application value

Respond ONLY with valid JSON, no other text.`;

return [{
  json: {
    notionId: notionId,
    notionUrl: notionUrl,
    tweetText: tweetText,
    userName: userName,
    claudeBody: {
      model: 'claude-haiku-4-5-20251001',
      max_tokens: 500,
      temperature: 0.3,
      messages: [{ role: 'user', content: prompt }]
    }
  }
}];

const claudeResponse = items[0].json.content?.[0]?.text || '';
const prepData = $node['Prepare Claude Request'].json;

let analysis;
try {
  const cleanJson = claudeResponse.replace(/```json\n?|```/g, '').trim();
  analysis = JSON.parse(cleanJson);

  const validCategories = ['Technique', 'Use Case', 'Tips', 'Examples', 'Tutorial', 'Inspiration', 'Discussion', 'Uncategorized'];
  if (!validCategories.includes(analysis.category)) {
    analysis.category = 'Uncategorized';
  }
  if (!Array.isArray(analysis.tags)) {
    analysis.tags = ['Needs-review'];
  }
} catch (error) {
  analysis = {
    category: 'Uncategorized',
    tags: ['Needs-review'],
    description: 'Failed to parse: ' + error.message,
    prompt: '',
    author: prepData.userName
  };
}

return [{
  json: {
    pageId: prepData.notionId,
    category: analysis.category,
    tags: analysis.tags,
    description: analysis.description || '',
    prompt: analysis.prompt || '',
    tweetContent: prepData.tweetText,
    author: prepData.userName,
    tweetUrl: prepData.notionUrl
  }
}];
